export default function Performance() {
  const platformPerformanceData = [
    {
      title: "Facebook",
      engagementRate: "45%",
      likes: "2.4K",
      comments: "1.2K",
      shares: "800",
    },
    {
      title: "Instagram",
      engagementRate: "50%",
      likes: "3.5K",
      comments: "2.1K",
      shares: "1.5K",
    },
    {
      title: "Twitter",
      engagementRate: "70%",
      likes: "1.8K",
      comments: "900",
      shares: "600",
    },
  ];

  return (
    <section className="my-4 bg-white flex flex-col md:flex-row gap-4 rounded-lg p-3">
      {/* left side */}
      <div className="p-4 w-full md:w-1/2 border-rounded-xl">
        <h3 className="text-[20px] font-semibold ">Platform Performance</h3>
        <div className="grid grid-cols-1 gap-4 mt-2">
          {platformPerformanceData.map((data, index) => (
            <div className="border border-gray-200 rounded-lg p-4">
              {/* platform name and logo */}
              <div className="inline-flex items-center rounded-full border-[#E9E9EA] mb-5 gap-1.5 border py-1 px-2.5">
                {data.title === "Facebook" && facebookLogo}
                {data.title === "Instagram" && instagramLogo}
                {data.title === "Twitter" && linkedinLogo}
                <p className="text-sm">{data.title}</p>
              </div>
              <div className="flex items-center justify-between mb-2">
                <h4 className="">Engagement Rate</h4>
                <h4 className="">{data.engagementRate}</h4>
              </div>
              <div className="h-1 w-full bg-gray-200 rounded-full mb-5">
                <div
                  className={`h-1 rounded-full  ${
                    data.title === "Facebook"
                      ? "bg-[#1877F2]"
                      : data.title === "Instagram"
                      ? "bg-[#FD65A9]"
                      : "bg-[#2E8EBD]"
                  }`}
                  style={{ width: data.engagementRate }}
                ></div>
              </div>
              <div className="grid grid-cols-1 lg:grid-cols-3 gap-2">
                <PerformanceCard title="Likes" count={data.likes} />
                <PerformanceCard title="Comments" count={data.comments} />
                <PerformanceCard title="Shares" count={data.shares} />
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* Right side */}
      <div></div>
    </section>
  );
}

const PerformanceCard = ({
  title,
  count,
}: {
  title: string;
  count: string;
}) => {
  return (
    <div className="flex items-center justify-between px-3 py-1 bg-[#F9F8FA] rounded-md">
      <div className="flex flex-col ">
        <p className="text-[#4A4C56]">{title}</p>
        <h3 className="font-semibold text-xl text-[#070707]">{count}</h3>
      </div>
    </div>
  );
};

